import * as BooksAPI from "../BooksAPI";
import BookView from './BookView';
import { Link } from "react-router-dom";
import { useState } from "react";
import PropTypes from 'prop-types';

const Search = ({ books }) => {
  const [query, setQuery] = useState('');
  const [queryBooks, setQueryBooks] = useState([]);

  const updateQuery = (query) => {
    setQuery(query);
    if (query.length > 0) {
      fetchQueryBooks(query);
    }
  };

  const fetchQueryBooks = (query) => {
    if (query.length > 0) {
      const setShow = async () => {
        const res = await BooksAPI.search(query);
        if (res.error) {
          setQueryBooks([]);
        } else {
          setQueryBooks(res);
        }
      }
      setShow();
    } else {
      setQueryBooks([]);
    }
  };

  const updatedQuery = queryBooks.map(queryBook => {
    books.map(book => {
      if (book.id === queryBook.id) {
        queryBook.shelf = book.shelf;
      }
      return book;
    }
    );
    return queryBook;
  });

  const setShelf = (queryBook) =>
    queryBook.shelf
      ? queryBook.shelf
      : 'none'

  return (

    <div className="search-books">
      <div className="search-books-bar">
        <Link className="close-search" to="/" >
          Back to Home
        </Link>
        <div className="search-books-input-wrapper">
          <input
            className="search-contacts"
            type="text"
            placeholder="Search books by Title or Author..."
            value={query}
            onChange={(event) => updateQuery(event.target.value)}
          />
        </div>
      </div>

      {
        query !== '' && (
          <div className="search-books-results">
            <div className="showing-books">
              <span>
                Showing {queryBooks.length} results for '{query}'
              </span>
              <button onClick={() => updateQuery('')}>Clear Search</button>
            </div>
            <ol className="books-grid">
              {/* first set the shelf for each of the query books then map them */}
              {updatedQuery.map((queryBook) => {
                return (
                  <BookView
                    currentBook={queryBook}
                    shelf={setShelf(queryBook)}
                    key={queryBook.id} />
                )
              })}
            </ol>
          </div>
        )}
    </div>
  )
};

Search.propTypes = {
  books: PropTypes.array.isRequired
}

export default Search;
