import PropTypes from 'prop-types';

const BookView = ({ currentBook }) => {

  const Thumbnail = (book) => {
    if (book.imageLinks) {
      return (book.imageLinks.smallThumbnail)
    } else { return ('') }
  };

  // const setShelf = (book) => {
  //   if (book.shelf) {
  //     return (book.shelf)
  //   } else { return ('none') }
  // };

  const setShelf = (book) => {
    return (
      book.shelf
      ? book.shelf
      : 'none'
    )};
  //   if (book.shelf) {
  //     return (book.shelf)
  //   } else { return ('none') }
  // };

  // const setShelf = (book) => {
  //   if (!book.shelf) {
  //     book.shelf = 'none'
  //   }
  // }

  // setShelf(currentBook)

  // currentBook.shelf
  //   ? currentBook.shelf = 'none'
  //   : currentBook.shelf

  return (
    <li>
      <div className="book">
        <div className="book-top">
          <div
            className="book-cover"
            style={{
              width: 128,
              height: 188,
              backgroundImage:
                `url(${Thumbnail(currentBook)})`,
            }}
          ></div>
          <div className="book-shelf-changer">
            <select>
              <option value="none" disabled>Move to...</option>
              <option value="currentlyReading">Currently Reading</option>
              <option value="wantToRead">Want to Read</option>
              <option value="read">Read</option>
              <option value="none">None</option>
            </select>
          </div>
        </div>
        <div className="book-title">{currentBook.title}</div>
        <div className="book-authors">{currentBook.authors}</div>
        <div className="book-authors">{setShelf(currentBook)}</div>
      </div>
    </li>
  )
};

BookView.propTypes = {
  currentBook: PropTypes.object.isRequired
};

export default BookView;
