import { Link } from "react-router-dom";
import { useState } from "react";
import * as BooksAPI from "../BooksAPI";
import PropTypes from 'prop-types';
import BookView from './BookView';

const Search = ({ books }) => {
  const [query, setQuery] = useState('');
  const [queryBooks, setQueryBooks] = useState([]);

  const updateQuery = (query) => {
    setQuery(query);
    fetchQueryBooks(query);
  };

  const clearQuery = () => {
    updateQuery('');
  };

  // fetching query books from API:
  // if invalid query setQueryBooks to []
  // if valid query setQueryBooks to the result
  const fetchQueryBooks = (query) => {
    if (query.length > 0) {
      const setShow = async () => {
        const res = await BooksAPI.search(query, 10);
        if (res.error) {
          setQueryBooks([]);
        } else {
          setQueryBooks(res);
        }
      }
      setShow();
    } else {
      setQueryBooks([]);
    }
  };

  // const updatedBooks = queryBooks.map(queryBook => {

  //   books.map(book => {
  //     if (book.id === queryBook.id) {
  //       queryBook.shelf = book.shelf;
  //     }
  //   });
  //   return queryBook;
  // });
  const updatedBooks = queryBooks.map(queryBook => {
    books.map((book) =>
      book.id === queryBook.id
      ? queryBook.shelf = book.shelf
      : 'none'
    );
    return queryBook;
  });

  // const showShelf =

  return (

    <div className="search-books">
      <div className="search-books-bar">
        <Link className="close-search" to="/" >
          Back to Home
        </Link>
        <div className="search-books-input-wrapper">
          <input
            className="search-contacts"
            type="text"
            placeholder="Search books by Title or Author..."
            value={query}
            onChange={(event) => updateQuery(event.target.value)}
          />
        </div>
      </div>

      {
        query !== '' && (
          <div className="search-books-results">
            <div className="showing-books">
              <span>
                Showing {queryBooks.length} results for '{query}'
              </span>
              <button onClick={() => clearQuery()}>Clear Search</button>
            </div>
            <ol className="books-grid">
              {updatedBooks.map((queryBook) => {
                return (
                  <BookView
                    currentBook={queryBook}
                    shelf={queryBook.shelf ? queryBook.shelf : 'none'} // need to filter for current shelves of books
                    key={queryBook.id} />
                )
              })}
            </ol>
          </div>
        )}
    </div>
  )
};

Search.propTypes = {
  books: PropTypes.array.isRequired
}

export default Search;
