    //       books.length > 0
    //         ? this.setState({ newBooks: books, searchErr: false })
    //         : this.setState({ newBooks: [], searchErr: true });
    //     });

    //     // if query is empty => reset state to default
    //   } else this.setState({ newBooks: [], searchErr: false });
    // };

  return (
    <div className="search-books">
      <div className="search-books-bar">
        <Link className="close-search" to="/" >
          Back to Home
        </Link>
        <div className="search-books-input-wrapper">
          <input
            className="search-contacts"
            type="text"
            placeholder="Search books by Title or Author..."
            value={query}
            onChange={(event) => updateQuery(event.target.value)}
          />
        </div>
      </div>

      <div className="search-books-results">
        {
          query !== '' && (
            <div className="showing-books">
              <span>
                Showing {'books.length'} results for '{query}'
              </span>
              <button onClick={() => clearQuery()}>Clear Search</button>
            </div>
          )
        }

        {/* Show books here based on search query */}

      </div>
    </div>
  )
};

export default AddBook;
