import { Link } from "react-router-dom";
import { useState, useEffect } from "react";
import * as BooksAPI from "../BooksAPI";

const Search = () => {
  const [query, setQuery] = useState('');
  const [books, setBooks] = useState([]);

  useEffect(() => {
    //logic for getting / setting contacts (could use promise or async/await)
    const getBooks = async () => {
      const res = await BooksAPI.getAll();
      setBooks(res);
    }
    // invoke the logic to get the books on first render:
    getBooks();
  }, [])

  const showingContacts =
    query === ""
      ? contacts
      : contacts.filter((c) =>
        c.name.toLowerCase().includes(query.toLowerCase())
      );

  const updateQuery = (query) => {
    setQuery(query.trim());
  };

  const clearQuery = () => {
    updateQuery('');
  };

  return (
    <div className="search-books">
      <div className="search-books-bar">
        <Link className="close-search" to="/" >
          Back to Home
        </Link>
        <div className="search-books-input-wrapper">
          <input
            className="search-contacts"
            type="text"
            placeholder="Search by title, author, or ISBN"
            value={query}
            onChange={(event) => updateQuery(event.target.value)}
          />
        </div>
      </div>
      <div className="search-books-results">
        {
          query !== '' && (
            <div className="showing-books">
              <span>
                Now showing {12} results for {query}
              </span>
              <button onClick={() => clearQuery()}>Show All</button>
            </div>
          )
        }
        <ol className="books-grid">
          <li>
            <div className="book">
              <div className="book-top">
                <div
                  className="book-cover"
                  style={{
                    width: 128,
                    height: 192,
                    backgroundImage:
                      // 'url("http://books.google.com/books/content?id=pD6arNyKyi8C&printsec=frontcover&img=1&zoom=1&imgtk=AFLRE70Rw0CCwNZh0SsYpQTkMbvz23npqWeUoJvVbi_gXla2m2ie_ReMWPl0xoU8Quy9fk0Zhb3szmwe8cTe4k7DAbfQ45FEzr9T7Lk0XhVpEPBvwUAztOBJ6Y0QPZylo4VbB7K5iRSk&source=gbs_api")',
                      `url(${books[0].imageLinks.smallThumbnail}`,
                  }}
                ></div>
                <div className="book-shelf-changer">
                  <select>
                    <option value="none" disabled>
                      Move to...
                    </option>
                    <option value="currentlyReading">
                      Currently Reading
                    </option>
                    <option value="wantToRead">Want to Read</option>
                    <option value="read">Read</option>
                    <option value="none">Remove</option>
                  </select>
                </div>
              </div>
              <div className="book-title">{books[0].title}</div>
              <div className="book-authors">{books[0].authors}</div>
            </div>
          </li>
          <li>
            <div>
              {console.log(Object.keys(books[0]))}
              {console.log(books.length)}
            </div>
          </li>
        </ol>
      </div>
    </div>
  )
};

export default Search;
