import "../App.css";
import { Route, Routes } from 'react-router-dom';
import ListBooks from "./ListBooks";
import Search from "./Search";

function App() {

  const addBook = (book) => {
    const create = async () => {
      const res = await ContactsAPI.add(book);
      //concat the API response (new contact) to contacts
      setContacts(contacts.concat(res));
    }
    //invoke to add the contact
    create();
    // take user back to List page
    navigate("/");

  };

  return (

    <Routes>
      <Route exact path="/"
        element={<ListBooks />}
      />
      <Route path="/Search"
        element={<Search />}
      />
    </Routes>

  );
}

export default App;
