import "../App.css";
import * as BooksAPI from "../BooksAPI";
import { Route, Routes } from 'react-router-dom';
import { useState, useEffect } from 'react';
import ListBooks from "./ListBooks";
import Search from "./Search";

function App() {
  const [allMyBooks, setallMyBooks] = useState([]);

  const shelves = [
    { key: 'currentlyReading', name: 'Currently Reading' },
    { key: 'wantToRead', name: 'Want to Read' },
    { key: 'read', name: 'Read' }
  ];

  // load books at first DOM render:
  useEffect(() => {
    const getBooks = async () => {
      const res = await BooksAPI.getAll();
      setAllMyBooks(res);
    }
    getBooks();
  }, [])

  const updateShelf = (book, shelf) => {
    const update = async () => {
      const res = await BooksAPI.update(book, shelf);
      setBooks(books.concat(res));
    }
    //invoke to update the book
    update();

  };

  return (

    <Routes>
      <Route exact path="/"
        element={<ListBooks books={books} onUpdateBook={updateShelf}/>}
      />
      <Route path="/Search"
        element={<Search books={books} onUpdateBook={updateShelf}/>}
      />
    </Routes>

  );
}

export default App;
